DECLARE

  v_membership_id 	NUMBER;
  v_season_pass_id 	NUMBER;
  v_day_pass_id 	NUMBER;
  v_deluxe_dine_id 	NUMBER;
  v_annual_dine_id 	NUMBER;
  v_season_drink_id NUMBER;
  v_day_drink_id 	NUMBER;
  v_day_dine_id 	NUMBER;
  v_day_flash_id 	NUMBER;
  v_parking_id 		NUMBER; 
  
BEGIN  

	SELECT ID INTO v_membership_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGS12MMEMB';
	SELECT ID INTO v_season_pass_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSSSNPASS';
	SELECT ID INTO v_day_pass_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSDAYPASS';
	SELECT ID INTO v_deluxe_dine_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSDXDINEPASS';
	SELECT ID INTO v_annual_dine_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSANDINEPASS';
	SELECT ID INTO v_season_drink_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSSSNDRINK';
	SELECT ID INTO v_day_drink_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSDAYDRINK';
	SELECT ID INTO v_day_dine_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSDAYDINE';
	SELECT ID INTO v_day_flash_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSDAYFLASH';
	SELECT ID INTO v_parking_id FROM T_LU_REWARDS_OFFER_TYPE WHERE TEMPLATE_TYPE_CODE = 'SIXFLAGSPARKING';


  COMMIT;

END;
/






























